mapper.select:

select rnum, board_no, member_id board_no, title, board_date, hit_cnt 
from (
	select rownum as rnum, board_no, member_id, title, board_date, hit_cnt
		from (
			select board_no, member_id, title, board_date, hit_cnt
			from intergrated_board
			where board_ctg=1
			order by board_no asc
		)
		where rownum<=#{endRowNo}
	) where rnum>=#{startRowNo}

mapper.update:
update intergrated_board set title = #{title}, content = #{content}

	, battach_oname=#{battachOname}
	, battach_type=#{battachType}
	, battach_data=#{battachData}


	, img_oname=#{imgOname}
	, img_type=#{imgType}
	, img_data=#{imgData}

where board_no = #{boardNo}
