mapper.update:
update educational_program
set program_title = #{programTitle},
act_bgn_date = #{actBgnDate},
act_end_date = #{actEndDate},
act_bgn_time = #{actBgnTime},
act_end_time = #{actEndTime},
recruit_region = #{recruitRegion},
recruit_bgn_date =
#{recruitBgnDate},
recruit_end_date = #{recruitEndDate},
recruit_cnt =
#{recruitCnt},
apply_cnt = #{applyCnt},
act_place = #{actPlace},
mng_name = #{mngName},
mng_tel = #{mngTel},
content = #{content}

	, battach_oname = #{battachOname}
	, battach_type =
	#{battachType}
	, battach_data = #{battachData}


	, img_oname = #{imgOname}
	, img_type = #{imgType}
	, img_data
	= #{imgData}

where program_no = #{programNo}

mapper.select:
select rnum, program_no, program_title, recruit_region, recruit_cnt, apply_cnt, recruit_bgn_date, recruit_end_date, act_bgn_date, act_end_date, act_bgn_time, act_end_time, act_place from ( select rownum as rnum, program_no, program_title, recruit_region, recruit_cnt, apply_cnt, recruit_bgn_date, recruit_end_date, act_bgn_date, act_end_date, act_bgn_time, act_end_time, act_place from educational_program where enabled = 1 and rownum <=#{pager.endRowNo}
and recruit_region = #{searchIndex.regionNo}
and program_title like '%' || #{searchIndex.keyword} || '%'
and recruit_stts = 1
and recruit_stts = 2
) where rnum >=#{pager.startRowNo}

mapper.update:
update educational_program set apply_cnt = apply_cnt + 1
recruit_stts = 2
where program_no = #{programNo}
