mapper.select:
select rnum, member_id, member_name, tel, email, reg_date
from (
select
rownum as rnum, member_id, member_name, tel, email, reg_date
from
member
where menabled = 1 and mrole = 'ROLE_USER'
and rownum <=
#{endRowNo}
)
where rnum >=
#{startRowNo}

mapper.update:
update member
set
member_pw = #{newPassword}
where
member_id = #{memberId}

update member
set
menabled = #{enabled}
where
member_id =
#{memberId}
